//		                                     																	                    																					  																													                    	        																																																																																																																																																																														        																																																																																															    															      }else {Vo =n*MC; VelBar.setValue((int)n);}}
//
//		                                     																	                    																					  																													                    	        																																																																																																																																																																														        																																																																																															    															      	if (aTextField == AnglText){
//		                                     																	                    																					  																													                    	        																																																																																																																																																																														        																																																																																															    															      			if ((n < 0.0) || (n > 89.99))
//		                                     																	                    																					  																													                    	        																																																																																																																																																																														        																																																																																															    															      						{MsgBox();
//		                                     																	                    																					  																													                    	        																																																																																																																																																																														        																																																																																															    															      										AnglText.setText(String.valueOf(AnglStart));	 
//		                                     																	                    																					  																													                    	        																																																																																																																																																																														        																																																																																															    															      														AnglBar.setValue((int)AnglStart);
//		                                     																	                    																					  																													                    	        																																																																																																																																																																														        																																																																																															    															      														      }else {Angl = n; AnglBar.setValue((int)n);}}
